  def calcFloor(instruction:String): Int = {
    instruction.map {
      case ')' => -1
      case '(' => 1
    }.sum
  }

  def calcEnterBasement(instructions:String, toFind:Int): Int = {
    instructions.zipWithIndex.foldLeft((0, -1)) { case ((currentFloor, foundAt), (instruction, index)) =>
      if(currentFloor == toFind) {
        (currentFloor, foundAt)
      } else {
        (currentFloor + (if(instruction == ')') -1 else 1), index)
      }
    }._2 + 1
  }

